async function logarUsuario(e) {
    e.preventDefault();

    try {
        // Aguarda a resposta da API
        const response = await api.get(`/api/usuarios?email=${email}`);
        
        // Verifica se o e-mail e senha correspondem
        if (response.data.email === email && response.data.senha === password) {
            Profile.setId(response.data.id);
            Profile.setName(response.data.nome);
            navigate('/usuario');
        } else {
            alert("Email ou senha incorretos");
        }
    } catch (error) {
        console.error("Erro ao encontrar usu치rio: ", error);
        alert("Erro ao encontrar usu치rio");
        console.log(`/api/usuarios?email=${email}`);
    }
}
